sqlMap.delete:
/* AdmCmmBoardDAO.cmmBoardDelete */

		DELETE FROM TB_HDA_CMM_BOARD
		WHERE CB_SEQ = #cbSeq#

sqlMap.insert:
/* AdmCmmBoardDAO.cmmBoardUpfileInsert */

		INSERT INTO TB_HDA_CB_UPFILE (
			CBUP_ORIGIN_FILENAME
			, CBUP_SAVE_FILEPATH
			, CB_SEQ
		) VALUES (
			#fileInfoVO.fileName#
			, #fileInfoVO.filePath#
			, #cbSeq#
		)

sqlMap.delete:
/* AdmCmmBoardDAO.cmmBoardUpfileDelete */

		DELETE FROM TB_HDA_CB_UPFILE
		WHERE CBUP_SEQ = #cbupSeq#

sqlMap.insert:
/* AdmCmmBoardDAO.cmmBoardCommentInsert */
INSERT INTO TB_HDA_CB_COMMENT ( CBCO_REG_SEQ , CBCO_REG_NAME , CBCO_CONTENT , CBCO_REG_DATE , CBCO_PASSWORD , CB_SEQ ) VALUES ( #adminVO.admSeq# , #adminVO.admName# , #cbCommentVO.cbcoContent# , CURRENT_TIMESTAMP , #cbCommentVO.cbcoPassword# , #cbCommentVO.cbSeq# )
select LAST_INSERT_ID() as value

sqlMap.delete:
/* AdmCmmBoardDAO.cmmBoardCommentDelete */

		DELETE FROM TB_HDA_CB_COMMENT
		WHERE CBCO_SEQ = #cbcoSeq#
