sqlMap.select:
/* Ech0105DAO.selectEch0105MtlList */
SELECT Z.* FROM ( SELECT ROW_NUMBER() OVER(order by A.RS_NO ) rownum , A.CORP_CD AS corpCd , F.CORP_NAME AS corpName , A.RS_NO AS rsNo , G.RS_NAME AS rsName , G.RS_CD AS rsCd , A.MTL_NO AS mtlNo , A.MTL_DSP AS mtlDsp , A.MTL_NAME AS mtlName , A.LOT_NO AS lotNo , A.MTL_SHP AS mtlShp , A.REMK AS remk , A.DATA_REGDT AS dataRegdt , A.DATA_REGNT AS dataRegnt , A.NC_YN AS ncYn , C.VEND_NAME AS vendName , B.RS_NO7 AS rsSeq , CONVERT(int, STUFF(MTL_DSP, 1, 1, '')) AS ord
) Z ORDER BY Z.corpCd , Z.rsNo , z.ncYn DESC , z.ord

/* Ech0105DAO.selectEch0105RsList */
SELECT Z.* FROM ( SELECT ROW_NUMBER() OVER(order by A.RS_NO DESC) rownum , A.CORP_CD AS corpCd , A.RS_NO7 AS rsNo7 , A.RS_NO AS rsNo , A.RS_CD AS rsCd , A.RS_NAME AS rsName , A.VMNG_NAME AS vmngName , B.VEND_NAME AS vendName
) Z ORDER BY Z.corpCd , Z.rsNo7

/* Ech0105DAO.selectEch0105MtlList2 */
SELECT Z.* FROM ( SELECT DISTINCT(CONCAT(A.MTL_DSP,A.MTL_NAME,A.LOT_NO,A.MTL_SHP,A.REMK)) AS dspKey , A.MTL_DSP AS mtlDsp , A.MTL_NAME AS mtlName , A.LOT_NO AS lotNo , A.MTL_SHP AS mtlShp , A.REMK AS remk
) Z ORDER BY Z.mtlDsp
