sqlMap.select:
/* Ech0205DAO.selectEch0205List */
SELECT Z.* FROM ( SELECT A.CORP_CD AS corpCd , A.RS_NO AS rsNo , A.BRANCH_CD AS branchCd , F.BRANCH_NAME AS branchName , A.RS_CD AS rsCd , A.REG_DT AS regDt , A.RS_STDT AS rsStdt , A.RS_ENDT AS rsEndt , A.RS_PTC AS rsPtc , A.RS_PPS AS rsPps , A.RS_NO3 AS rsNo3 , K.CD_NAME AS rsNo3Nm , A.RS_DRT AS rsDrt , A.RS_SCNT AS rsScnt , A.RS_MSCNT AS rsMscnt , A.RSST_CLS AS rsStCls , G.CD_NAME AS rsstClsNm , A.REP_DT AS repDt , A.VISIT_CNT AS visitCnt , A.DUPL_YN AS duplYn , A.RS_NAME AS rsName , A.RS_PAY AS rsPay , A.IRBSM_YN AS irbsmYn , A.ITEM_CLS AS itemCls , U.CD_NAME AS itemClsNm , A.ITEM_NAME AS itemName , A.VEND_NO AS vendNo , V.VEND_NAME AS vendName , A.VMNGHP_NO AS vmnghpNo , A.VMNG_EMAIL AS vmngEmail , A.DATA_LOCK_YN AS dataLockYn , A.DEL_YN AS delYn , (SELECT count(*) from CR3210M cm WHERE cm.CORP_CD = A.CORP_CD AND cm.RS_NO = A.RS_NO AND cm.CHK_CNT IS NOT NULL) AS usechkCnt , H.USE_YN AS useYn , CASE H.USE_YN WHEN 'Y' THEN '사용' WHEN 'N' THEN '대기' ELSE '미정' END AS useYnNm
AND A.DEL_YN = 'N' AND A.RS_NO2 = 'E'
) Z ORDER BY Z.rsNo DESC OFFSET #firstIndex# ROWS FETCH NEXT #recordCountPerPage# ROWS ONLY

/* Ech0205DAO.selectEch0205Excel */
SELECT Z.* FROM ( SELECT A.CORP_CD AS corpCd , A.RS_NO AS rsNo , A.BRANCH_CD AS branchCd , F.BRANCH_NAME AS branchName , A.RS_CD AS rsCd , A.REG_DT AS regDt , A.RS_STDT AS rsStdt , A.RS_ENDT AS rsEndt , A.RS_PTC AS rsPtc , A.RS_PPS AS rsPps , A.RS_NO3 AS rsNo3 , K.CD_NAME AS rsNo3Nm , A.RS_DRT AS rsDrt , A.RS_SCNT AS rsScnt , A.RS_MSCNT AS rsMscnt , A.RSST_CLS AS rsStCls , G.CD_NAME AS rsstClsNm , A.REP_DT AS repDt , A.VISIT_CNT AS visitCnt , A.DUPL_YN AS duplYn , A.RS_NAME AS rsName , A.RS_PAY AS rsPay , A.IRBSM_YN AS irbsmYn , A.ITEM_CLS AS itemCls , U.CD_NAME AS itemClsNm , A.ITEM_NAME AS itemName , A.VEND_NO AS vendNo , V.VEND_NAME AS vendName , A.VMNGHP_NO AS vmnghpNo , A.VMNG_EMAIL AS vmngEmail , A.DATA_LOCK_YN AS dataLockYn , A.DEL_YN AS delYn , (SELECT count(*) from CR3210M cm WHERE cm.CORP_CD = A.CORP_CD AND cm.RS_NO = A.RS_NO AND cm.CHK_CNT IS NOT NULL) AS usechkCnt , H.USE_YN AS useYn , CASE H.USE_YN WHEN 'Y' THEN '사용' WHEN 'N' THEN '대기' ELSE '미정' END AS useYnNm
AND A.DEL_YN = 'N' AND A.RS_NO2 = 'E'
) Z ORDER BY Z.rsNo DESC

/*Ech0205DAO.selectEch0205ListCnt */
SELECT count(*) totalCnt
AND A.DEL_YN = 'N' AND A.RS_NO2 = 'E'

/* Ech0205DAO.selectEch0205ChkList */
SELECT Z.* FROM ( SELECT ROW_NUMBER() OVER(order by A.RS_NO DESC) rownum , A.CORP_CD AS corpCd , A.RS_NO AS rsNo , A.SUB_NO AS subNo , A.RSJ_NO AS rsjNo , A.RSI_NO AS rsiNo , C.RSJ_NAME AS rsjName , C.BR_DT AS brDt , FLOOR(CAST(DATEDIFF(DAY,C.BR_DT,GETDATE()) AS INTEGER)/365.2422) AS age , C.GEN_YN AS genYn , CASE C.GEN_YN WHEN '1' THEN '남' WHEN '2' THEN '여' END AS genYnNm , C.HP_NO AS hpNo , B.RE_YN AS reYn , B.ST_YN AS stYn , B.REMK AS remk , B.CHK_STDT AS chkStdt , B.CHK_ENDT AS chkEndt , B.CHK_CNT AS chkCnt , (select ISNULL(sum(z.c1),0)+ISNULL(sum(z.c2),0)+ISNULL(sum(z.c3),0)+ISNULL(sum(z.c4),0)+ISNULL(sum(z.c5),0) AS unUseCnt from ( SELECT Case WHEN chk1 = 'N' THEN 1 END as c1 , Case WHEN chk2 = 'N' THEN 1 END as c2 , Case WHEN chk3 = 'N' THEN 1 END as c3 , Case WHEN chk4 = 'N' THEN 1 END as c4 , Case WHEN chk5 = 'N' THEN 1 END as c5 FROM CR3240M D WHERE D.CORP_CD = A.CORP_CD AND D.RS_NO = A.RS_NO AND D.RSJ_NO = A.RSJ_NO AND (CHK1 = 'N' OR CHK2 = 'N' OR CHK3 = 'N' OR CHK4 = 'N' OR CHK5 = 'N')) z ) AS unUseCnt
) Z ORDER BY Z.corpCd , z.rsNo , z.rsiNo

