sqlMap.insert:
/* Ech0205DAO.insertEch0205AjaxSaveStep */ INSERT CR3210M( CORP_CD , RS_NO , RSJ_NO , CHK_STDT , CHK_ENDT , CHK_CNT , TCHK_CNT , RE_YN , ST_YN , REMK , DATA_REGDT , DATA_REGNT) SELECT A.CORP_CD , A.RS_NO , A.RSJ_NO , #chkStdt# , #chkEndt# , 0 , ((DATEDIFF(DD, B.CHK_STDT, B.CHK_ENDT) + 1)/B.CHK_TRM) * B.CHK_TRM * B.CHK_CNT , '' , 'N' ,'' , GETDATE() , #dataRegnt# FROM RS2000M A INNER JOIN cr3200M B on B.CORP_CD = A.CORP_CD and B.RS_NO = A.RS_NO WHERE A.CORP_CD = #corpCd# AND A.RS_NO = #rsNo# AND CONCAT(A.RS_NO,A.RSJ_NO)
#rsjSeq[]#
AND A.CFM_YN = 'Y' AND A.RSI_NO3 IS NOT NULL

sqlMap.select:
/*Ech0205DAO.selectEch0205AjaxSaveStepCnt */
SELECT count(*) totalCnt FROM CR3210M WHERE CORP_CD = #corpCd# AND CONCAT(RS_NO,RSJ_NO)
#rsjSeq[]#

sqlMap.update:
