#rsjSeq[]#

sqlMap.select:
/* Ech0205DAO.selectEch0205RsjUseView */
SELECT A.CORP_CD AS corpCd , A.RS_NO AS rsNo , A.RSJ_NO AS rsjNo , A.CHK_STDT AS chkStdt , A.CHK_ENDT AS chkEndt , A.CHK_CNT AS chkCnt , A.TCHK_CNT AS tchkCnt , A.RE_YN AS reYn , A.ST_YN AS stYn , A.REMK AS remk , A.DATA_REGDT AS dataRegdt , A.DATA_REGNT AS dataRegnt , B.RSJ_NAME AS rsjName , B.BR_DT AS brDt , FLOOR(CAST(DATEDIFF(DAY,B.BR_DT,GETDATE()) AS INTEGER)/365.2422) AS age , HP_NO AS hpNo , CASE B.GEN_YN WHEN '1' THEN '남' WHEN '2' THEN '여' END AS genYnNm FROM CR3210M A INNER JOIN SB1000M B ON B.CORP_CD = A.CORP_CD AND B.RSJ_NO = A.RSJ_NO INNER JOIN RS1000M F ON F.CORP_CD = A.CORP_CD AND F.RS_NO = A.RS_NO
WHERE A.CORP_CD = #corpCd# AND A.RS_NO = #rsNo# AND A.RSJ_NO = #rsjNo#
