sqlMap.select:
/* Ecr0101DAO.selectEcr0101List */
SELECT Z.* FROM ( SELECT A.CORP_CD AS corpCd , A.RS_NO AS rsNo , A.BRANCH_CD AS branchCd , F.BRANCH_NAME AS branchName , A.RS_CD AS rsCd , A.REG_DT AS regDt , A.RS_STDT AS rsStdt , A.RS_ENDT AS rsEndt , A.RS_PTC AS rsPtc , A.RS_PPS AS rsPps , A.RS_NO3 AS rsNo3 , K.CD_NAME AS rsNo3Nm , A.RS_DRT AS rsDrt , A.RS_SCNT AS rsScnt , A.RS_MSCNT AS rsMscnt , A.RSST_CLS AS rsStCls , G.CD_NAME AS rsstClsNm , A.REP_DT AS repDt , A.VISIT_CNT AS visitCnt , A.DUPL_YN AS duplYn , A.RS_NAME AS rsName , A.RS_PAY AS rsPay , A.IRBSM_YN AS irbsmYn , A.ITEM_CLS AS itemCls , U.CD_NAME AS itemClsNm , A.ITEM_NAME AS itemName , A.VEND_NO AS vendNo , V.VEND_NAME AS vendName , A.VMNGHP_NO AS vmnghpNo , A.VMNG_EMAIL AS vmngEmail , A.DATA_LOCK_YN AS dataLockYn , A.DEL_YN AS delYn , (SELECT COUNT(*) FROM RS2010M L WHERE L.CORP_CD = A.CORP_CD AND L.RS_NO = A.RS_NO) AS usechkCnt , W.VEND AS vendName2 , A.MK_YN AS mkYn , A.MK_CLS AS mkCls , CONCAT(LOWER(A.CORP_CD),LOWER(A.RS_NO)) AS mapKey
AND A.DEL_YN = 'N'
) Z ORDER BY Z.rsNo DESC OFFSET #firstIndex# ROWS FETCH NEXT #recordCountPerPage# ROWS ONLY

/* Ecr0101DAO.selectEcr0101Excel */
SELECT Z.* FROM ( SELECT A.CORP_CD AS corpCd , A.RS_NO AS rsNo , A.BRANCH_CD AS branchCd , F.BRANCH_NAME AS branchName , A.RS_CD AS rsCd , A.REG_DT AS regDt , A.RS_STDT AS rsStdt , A.RS_ENDT AS rsEndt , A.RS_PTC AS rsPtc , A.RS_PPS AS rsPps , A.RS_NO3 AS rsNo3 , K.CD_NAME AS rsNo3Nm , A.RS_DRT AS rsDrt , A.RS_SCNT AS rsScnt , A.RS_MSCNT AS rsMscnt , A.RSST_CLS AS rsStCls , G.CD_NAME AS rsstClsNm , A.REP_DT AS repDt , A.VISIT_CNT AS visitCnt , A.DUPL_YN AS duplYn , A.RS_NAME AS rsName , A.RS_PAY AS rsPay , A.IRBSM_YN AS irbsmYn , A.ITEM_CLS AS itemCls , U.CD_NAME AS itemClsNm , A.ITEM_NAME AS itemName , A.VEND_NO AS vendNo , V.VEND_NAME AS vendName , A.VMNGHP_NO AS vmnghpNo , A.VMNG_EMAIL AS vmngEmail , A.DATA_LOCK_YN AS dataLockYn , A.DEL_YN AS delYn , A.MK_YN AS mkYn , A.MK_CLS AS mkCls , (SELECT COUNT(*) FROM RS2010 L WHERE L.CORP_CD = A.CORP_CD AND L.RS_NO = A.RS_NO) AS usechkCnt
AND A.DEL_YN = 'N'
) Z ORDER BY Z.rsNo DESC

/*Ecr0101DAO.selectEcr0101ListCnt */
