AND A.DEL_YN = 'N'

/* Ecr0101DAO.selectEcr0101ChkList */
SELECT Z.* FROM ( SELECT ROW_NUMBER() OVER(order by A.RS_NO DESC) rownum , A.CORP_CD AS corpCd , A.RS_NO AS rsNo , A.SUB_NO AS subNo , A.RSJ_NO AS rsjNo , A.RSI_NO AS rsiNo , A.RSI_NO3 AS rsiNo3 , A.RSJ_NAME AS rsjName , C.BR_DT AS brDt , FLOOR(CAST(DATEDIFF(DAY,C.BR_DT,GETDATE()) AS INTEGER)/365.2422) AS age , A.GEN_YN AS genYn , CASE A.GEN_YN WHEN '1' THEN '남' WHEN '2' THEN '여' END AS genYnNm , A.AGE AS ageDisp , C.HP_NO AS hpNo , A.ETC AS etc , (SELECT COUNT(*) FROM CR2110M D WHERE D.CORP_CD = A.CORP_CD AND D.RS_NO = A.RS_NO) AS tmplCnt , (SELECT COUNT(*) FROM CR2120M E WHERE E.CORP_CD = A.CORP_CD AND E.RS_NO = A.RS_NO AND E.RSI_NO = A.RSI_NO AND E.MK_YN = 'Y') AS mkCnt , (SELECT COUNT(*) FROM CR2120M F INNER JOIN CR2100M G ON G.CORP_CD = F.CORP_CD AND G.TEMP_NO = F.TEMP_NO AND G.TEMP_TYPE = '1030' WHERE F.CORP_CD = A.CORP_CD AND F.RS_NO = A.RS_NO AND F.RSI_NO = A.RSI_NO AND F.MK_YN = 'Y') AS mkFinCnt , D.RS_NO5 AS rsNo5 , D.RS_NO6 AS rsNo6 , A.DOUT_YN AS doutYn , A.DOUT_CLS AS doutCls , A.DOUT_CONT AS doutCont , A.DOUT_DT AS doutDt , CONCAT(LOWER(A.CORP_CD),LOWER(A.RS_NO),LOWER(A.RSI_NO)) AS mapKey
) Z ORDER BY Z.corpCd , z.rsNo , z.rsiNo3

