sqlMap.select:
/* CmmDAO.selectAttachList */
SELECT COUNT(*) totalCnt
FROM CT7000M WHERE BOARD_NO = #boardSeq#

sqlMap.insert:
/* CmmDAO.insertCmmAjaxSaveSms */
DECLARE @R_CORP_CD NVARCHAR(0008) = #corpCd# DECLARE @V_RECM_DT NVARCHAR(0010) = #recsDt# DECLARE @P_RTN_KEY NVARCHAR(0020) DECLARE @BDT04 NVARCHAR(0004) DECLARE @BDT06 NVARCHAR(0006) DECLARE @BDT10 NVARCHAR(0010) SET @BDT10 = CONVERT(NVARCHAR(10), GETDATE(), 23) SET @BDT04 = SUBSTRING(@V_RECM_DT, 1, 4) SET @BDT06 = SUBSTRING(DBO.UFN_DTG08(@BDT10), 1, 6)
SELECT CONVERT(NVARCHAR, CONVERT(DECIMAL(18, 0), ISNULL(MAX(A.RECS_NO), @BDT04 + '0000')) + 1) as recsNo FROM RM2000M A WHERE A.CORP_CD = @R_CORP_CD AND SUBSTRING(A.RECS_DT, 1, 4) = @BDT04
/* CmmDAO.insertCmmAjaxSaveSms */
INSERT RM2000M( CORP_CD , RECS_NO , RECS_DT , TSTA_CLS , TSEN_CLS , SEND_NO , SEND_NAME , RECE_NO , RECE_NAME , SMS_CLS , CONT , SENDM_CLS , RS_NO , RESR_DT , RESR_HR , RESR_MM , SEND_DT , RSJ_NO , BRANCH_CD , REMK , ACCP_DT , RETN_DT , DATA_REGDT , DATA_REGNT ) VALUES( #corpCd# , #recsNo# , #recsDt# , #tstaCls# , #tsenCls# , #sendNo# , #sendName# , #receNo# , #receName# , #smsCls# , #cont# , #sendmCls# , #rsNo# , #resrDt# , #resrHr# , #resrMm# , GETDATE() , #rsjNo# , #branchCd# , #remk# , #accpDt# , #retnDt# , GETDATE() , #dataRegnt# )
